import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";

const EditStudent = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const [formData, setFormData] = useState({
    fullname: "",
    address: "",
    phoneNumber: "",
    birthDate: "",
    gender: "",
    programStudy: "",
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch data student berdasarkan id
    fetch(`http://localhost:3000/students/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setFormData({
          fullname: data.fullname,
          address: data.address,
          phoneNumber: data.phoneNumber,
          birthDate: data.birthDate,
          gender: data.gender,
          programStudy: data.programStudy,
        });
        setLoading(false);
      })
      .catch((error) => {
        console.error("Terjadi kesalahan saat mengambil data student:", error);
        setLoading(false);
      });
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const { fullname, address, phoneNumber, birthDate, gender, programStudy } = formData;

    const studentData = {
      fullname,
      address,
      phoneNumber,
      birthDate,
      gender,
      programStudy,
    };

    // Kirim permintaan PUT ke json-server
    fetch(`http://localhost:3000/students/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(studentData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Data student berhasil diubah:", data);
        // Redirect ke halaman Student
        navigate("/student");
      })
      .catch((error) => {
        console.error("Terjadi kesalahan saat mengubah data student:", error);
      });
  };

  if (loading) {
    return <p>Loading ...</p>;
  }

  return (
    <>
      <h2>Edit Student</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="fullname">Full Name</label>
          <input
            type="text"
            id="fullname"
            value={formData.fullname}
            onChange={(e) => setFormData({ ...formData, fullname: e.target.value })}
            required
          />
        </div>
        <div>
          <label htmlFor="address">Address</label>
          <input
            type="text"
            id="address"
            value={formData.address}
            onChange={(e) => setFormData({ ...formData, address: e.target.value })}
            required
          />
        </div>
        <div>
          <label htmlFor="phoneNumber">Phone Number</label>
          <input
            type="text"
            id="phoneNumber"
            value={formData.phoneNumber}
            onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })}
            required
          />
        </div>
        <div>
          <label htmlFor="birthDate">Birth Date</label>
          <input
            type="date"
            id="birthDate"
            value={formData.birthDate}
            onChange={(e) => setFormData({ ...formData, birthDate: e.target.value })}
            required
          />
        </div>
        <div>
          <label htmlFor="gender">Gender</label>
          <select
            id="gender"
            value={formData.gender}
            onChange={(e) => setFormData({ ...formData, gender: e.target.value })}
            required
          >
            <option value="">-- Select Gender --</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>
        <div>
          <label htmlFor="programStudy">Program Study</label>
          <select
            id="programStudy"
            value={formData.programStudy}
            onChange={(e) => setFormData({ ...formData, programStudy: e.target.value })}
            required
          >
            <option value="">-- Select Program Study --</option>
            <option value="Ekonomi">Ekonomi</option>
            <option value="Manajemen">Manajemen</option>
            <option value="Akuntansi">Akuntansi</option>
            <option value="Administrasi Publik">Administrasi Publik</option>
            <option value="Administrasi Bisnis">Administrasi Bisnis</option>
            <option value="Hubungan Internasional">Hubungan Internasional</option>
            <option value="Teknik Sipil">Teknik Sipil</option>
            <option value="Arsitektur">Arsitektur</option>
            <option value="Matematika">Matematika</option>
            <option value="Fisika">Fisika</option>
            <option value="Informatika">Informatika</option>
          </select>
        </div>
        <div>
          <button type="submit" data-testid="edit-btn">Edit Student</button>
        </div>
      </form>
    </>
  );
};

export default EditStudent;
